import { Component, OnInit } from '@angular/core';
import {CandidateService} from "../../services/candidate.service";
import {Candidate} from "../../models/candidate.interface";
import {NgbModal} from "@ng-bootstrap/ng-bootstrap";
import {Observable} from "rxjs";

@Component({
  selector: 'app-admin-panel',
  templateUrl: './admin-panel.component.html',
  styleUrls: ['./admin-panel.component.scss']
})
export class AdminPanelComponent implements OnInit {

  candidates$: Observable<Candidate[]>;
  name: string;
  // true add, false edit
  flag: boolean = true;
  saveCand: any;
  counter = 0;

  constructor(private candidateService: CandidateService, private modalService: NgbModal) { }

  ngOnInit(): void {
    this.candidates$ = this.candidateService.getAllCandidates();
  }

  open(content: any, id: number) {
    if(id != null) {
      const findCandidate = this.candidates.find(el => el.id == id);
      if(findCandidate) {
        this.name = findCandidate.name;
      }

      this.flag = false;
      this.saveCand = findCand;
    } else {
      this.flag = true;
      this.saveCand = '';
      this.name = '';
    }
    this.modalService.open(content, {ariaLabelledBy: 'modal-basic-title'}).result.then((result) => {
    });
  }

  saveCandidate() {
    // if(this.flag) {
    //   const newCandidate = {
    //     name: this.name,
    //     index: this.counter
    //   }
    //   this.localStorageService.addNewCandidate(newCandidate);
    //   this.counter++;
    // } else {
    //   this.localStorageService.editCandidate(this.saveCand, this.name)
    // }
    // this.candidates = this.localStorageService.getCandidates();
    // this.close();
  }

  close() {
    this.modalService.dismissAll();
  }

  delete(name: any) {
    // this.localStorageService.deleteCandidate(name);
    // this.candidates = this.localStorageService.getCandidates();
  }

}
<div class="container">

  <h1>Admin Panel</h1>

  <button class="btn btn-primary mt-2 mb-2" (click)="open(mymodal, null)">Add new candidate</button>

  <p>List of the active candidates:</p>

  <table class="table table-hover table-striped" *ngIf="(candidates$ | async).length > 0">
    <thead>
    <tr>
      <th scope="col">Name</th>
      <th scope="col">Edit</th>
      <th scope="col">Delete</th>
    </tr>
    <tr *ngFor="let c of candidates$ | async">
      <td>
        <span>{{ c.name }}</span>
      </td>
      <td class="link" (click)="open(mymodal, c.id)">Edit</td>
      <td class="link" (click)="delete(c.id)" >Delete</td>
    </tr>
    </thead>
    <tbody>

    </tbody>
  </table>

  <ng-template #mymodal let-modal>
    <div class="modal-header">
      <h4 class="modal-title" id="modal-basic-title">Add new candidate</h4>
      <button type="button" class="close" aria-label="Close" (click)="modal.dismiss('Cross click')"  data-bs-dismiss="modal">
        <span aria-hidden="true">×</span>
      </button>
    </div>
    <div class="modal-body">
      <div class="form-group">
        <label>Name</label>
        <input type="text" class="form-control" [(ngModel)]="name" required>
      </div>

    </div>
    <div class="modal-footer">
      <button type="button" class="btn btn-outline-dark" (click)="saveCandidate()">Save</button>
    </div>
  </ng-template>
</div>
